import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Emprestimos = () => {
  const [emprestimos, setEmprestimos] = useState([]);

  useEffect(() => {
    // Buscar empréstimos
    const fetchEmprestimos = async () => {
      try {
        const resposta = await axios.get('http://localhost:3001/api/emprestimos');
        setEmprestimos(resposta.data);
      } catch (erro) {
        console.error('Erro ao buscar empréstimos:', erro);
      }
    };

    fetchEmprestimos();
  }, []);

  const finalizarEmprestimo = async (id) => {
    try {
      const resposta = await axios.put(`http://localhost:3001/api/emprestimos/${id}`);
      console.log('Devolução registrada:', resposta.data);
      // Atualize o estado ou recarregue os dados após a devolução
      setEmprestimos(emprestimos.filter((emprestimo) => emprestimo._id !== id));
    } catch (erro) {
      console.error('Erro ao finalizar empréstimo:', erro);
    }
  };

  return (
    <div>
      <h1>Empréstimos</h1>
      <table>
        <thead>
          <tr>
            <th>Nome do Usuário</th>
            <th>Título do Livro</th>
            <th>Data de Devolução</th>
            <th>Devolver</th>
          </tr>
        </thead>
        <tbody>
          {emprestimos.map((emprestimo) => (
            <tr key={emprestimo._id}>
              <td>{emprestimo.usuario.nome}</td>
              <td>{emprestimo.livro.titulo}</td>
              <td>{emprestimo.dataDevolucao}</td>
              <td>
                <button onClick={() => finalizarEmprestimo(emprestimo._id)}>Devolver</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Emprestimos;
