import './Relatorios.css'; // Importando o CSS da página Relatórios
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Relatorios = () => {
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);
  const [usuariosComPendencias, setUsuariosComPendencias] = useState([]);

  // Buscar livros mais emprestados
  const fetchLivrosMaisEmprestados = async () => {
    try {
      const response = await axios.get('/api/emprestimos/relatorio/livros-mais-emprestados');
      setLivrosMaisEmprestados(response.data);
    } catch (error) {
      console.error('Erro ao buscar livros mais emprestados:', error);
    }
  };

  // Buscar usuários com pendências
  const fetchUsuariosComPendencias = async () => {
    try {
      const response = await axios.get('/api/emprestimos/relatorio/usuarios-com-pendencias');
      setUsuariosComPendencias(response.data);
    } catch (error) {
      console.error('Erro ao buscar usuários com pendências:', error);
    }
  };

  useEffect(() => {
    fetchLivrosMaisEmprestados();
    fetchUsuariosComPendencias();
  }, []);

  return (
    <div>
      <h2>Relatórios</h2>
      
      <div>
        <h3>Livros mais emprestados</h3>
        <table>
          <thead>
            <tr>
              <th>Título</th>
              <th>Autor</th>
              <th>Quantidade de Empréstimos</th>
            </tr>
          </thead>
          <tbody>
            {livrosMaisEmprestados.map((item) => (
              <tr key={item._id}>
                <td>{item.livro.titulo}</td>
                <td>{item.livro.autor}</td>
                <td>{item.count}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div>
        <h3>Usuários com pendências</h3>
        <table>
          <thead>
            <tr>
              <th>Nome</th>
              <th>Email</th>
              <th>Telefone</th>
              <th>Título do Livro</th>
            </tr>
          </thead>
          <tbody>
            {usuariosComPendencias.map((item) => (
              <tr key={item._id}>
                <td>{item.usuario.nome}</td>
                <td>{item.usuario.email}</td>
                <td>{item.usuario.telefone}</td>
                <td>{item.livro.titulo}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Relatorios;
