import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Relatorios = () => {
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);
  const [usuariosComPendencias, setUsuariosComPendencias] = useState([]);

  useEffect(() => {
    fetchLivrosMaisEmprestados();
    fetchUsuariosComPendencias();
  }, []);

  const fetchLivrosMaisEmprestados = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/emprestimos/relatorios/livros-mais-emprestados');
      setLivrosMaisEmprestados(response.data);
    } catch (error) {
      console.error('Erro ao buscar relatório de livros mais emprestados:', error);
    }
  };

  const fetchUsuariosComPendencias = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/emprestimos/relatorios/usuarios-com-pendencias');
      setUsuariosComPendencias(response.data);
    } catch (error) {
      console.error('Erro ao buscar relatório de usuários com pendências:', error);
    }
  };

  return (
    <div className="relatorios-container">
      <h1>Relatórios</h1>

      <h2>Livros Mais Emprestados</h2>
      <ul>
        {livrosMaisEmprestados.map((item) => (
          <li key={item._id}>
            {item.livro.titulo} - {item.total} empréstimos
          </li>
        ))}
      </ul>

      <h2>Usuários com Empréstimos Pendentes</h2>
      <ul>
        {usuariosComPendencias.map((item) => (
          <li key={item._id}>
            {item.usuario.nome} ({item.usuario.email}) - Livro: {item.livro.titulo}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Relatorios;
