import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '/Relatorios.css';

const Relatorios = () => {
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);
  const [usuariosComPendencias, setUsuariosComPendencias] = useState([]);

  useEffect(() => {
    // Fetch livros mais emprestados
    axios
      .get('http://localhost:3000/api/emprestimos/relatorio/livros-mais-emprestados')
      .then(response => setLivrosMaisEmprestados(response.data))
      .catch(error => console.error('Erro ao carregar livros mais emprestados:', error));

    // Fetch usuários com pendências
    axios
      .get('http://localhost:3000/api/emprestimos/relatorio/usuarios-com-pendencias')
      .then(response => setUsuariosComPendencias(response.data))
      .catch(error => console.error('Erro ao carregar usuários com pendências:', error));
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Relatórios</h1>

      <section>
        <h2>Livros Mais Emprestados</h2>
        {livrosMaisEmprestados.length > 0 ? (
          <table border="1">
            <thead>
              <tr>
                <th>Título</th>
                <th>Autor</th>
                <th>Quantidade de Empréstimos</th>
              </tr>
            </thead>
            <tbody>
              {livrosMaisEmprestados.map(item => (
                <tr key={item._id._id}>
                  <td>{item._id.titulo}</td>
                  <td>{item._id.autor}</td>
                  <td>{item.count}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhum dado encontrado.</p>
        )}
      </section>

      <section style={{ marginTop: '40px' }}>
        <h2>Usuários com Empréstimos Pendentes</h2>
        {usuariosComPendencias.length > 0 ? (
          <table border="1">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Email</th>
                <th>Telefone</th>
                <th>Livro</th>
              </tr>
            </thead>
            <tbody>
              {usuariosComPendencias.map(item => (
                <tr key={item._id}>
                  <td>{item.usuario.nome}</td>
                  <td>{item.usuario.email}</td>
                  <td>{item.usuario.telefone}</td>
                  <td>{item.livro.titulo}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhum dado encontrado.</p>
        )}
      </section>
    </div>
  );
};

export default Relatorios;
