import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RelatorioUsuariosComPendencias = () => {
  const [usuariosComPendencias, setUsuariosComPendencias] = useState([]);

  useEffect(() => {
    const fetchUsuariosComPendencias = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/relatorios/usuarios-com-pendencias');
        setUsuariosComPendencias(response.data);
      } catch (error) {
        console.error('Erro ao buscar relatórios de usuários com pendências:', error);
      }
    };

    fetchUsuariosComPendencias();
  }, []);

  return (
    <div>
      <h1>Relatório: Usuários com Pendências</h1>
      <ul>
        {usuariosComPendencias.map((pendencia) => (
          <li key={pendencia._id}>
            <strong>{pendencia.usuario.nome}</strong> - {pendencia.livro.titulo} - Data de Devolução: {new Date(pendencia.dataDevolucao).toLocaleDateString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RelatorioUsuariosComPendencias;
