import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Relatorios.css'; // Importe o CSS da página Home

const Relatorios = () => {
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);
  const [usuariosComPendencias, setUsuariosComPendencias] = useState([]);
  
  // Função para buscar os livros mais emprestados
  const fetchLivrosMaisEmprestados = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/emprestimos/livros-mais-emprestados');
      setLivrosMaisEmprestados(response.data);
    } catch (error) {
      console.error('Erro ao buscar livros mais emprestados:', error);
    }
  };

  // Função para buscar os usuários com pendências
  const fetchUsuariosComPendencias = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/emprestimos/usuarios-com-pendencias');
      setUsuariosComPendencias(response.data);
    } catch (error) {
      console.error('Erro ao buscar usuários com pendências:', error);
    }
  };

  // Chama as funções ao carregar a página
  useEffect(() => {
    fetchLivrosMaisEmprestados();
    fetchUsuariosComPendencias();
  }, []);

  return (
    <div className="relatorios-container">
      <h1>Relatórios de Biblioteca</h1>
      
      <section className="relatorio">
        <h2>Livros Mais Emprestados</h2>
        {livrosMaisEmprestados.length > 0 ? (
          <ul>
            {livrosMaisEmprestados.map((item, index) => (
              <li key={index}>
                <strong>{item.livroInfo.titulo}</strong> - Emprestado <span>{item.count}</span> vezes
              </li>
            ))}
          </ul>
        ) : (
          <p>Não há dados disponíveis.</p>
        )}
      </section>

      <section className="relatorio">
        <h2>Usuários com Empréstimos Pendentes</h2>
        {usuariosComPendencias.length > 0 ? (
          <ul>
            {usuariosComPendencias.map((pendencia, index) => (
              <li key={index}>
                <strong>{pendencia.usuario.nome}</strong> - Livro: {pendencia.livro.titulo} - Devolução: {pendencia.dataDevolucao}
              </li>
            ))}
          </ul>
        ) : (
          <p>Não há usuários com pendências no momento.</p>
        )}
      </section>
    </div>
  );
};

export default Relatorios;
