import React, { useState } from 'react';
import axios from 'axios';

const Emprestimos = () => {
  const [emprestimos, setEmprestimos] = useState([]);
  
  // Carregar empréstimos
  const loadEmprestimos = async () => {
    try {
      const response = await axios.get('/api/emprestimos');
      setEmprestimos(response.data);
    } catch (error) {
      console.error('Erro ao carregar empréstimos:', error);
    }
  };

  // Finalizar empréstimo (devolução)
  const finalizarEmprestimo = async (id) => {
    try {
      const response = await axios.put(`/api/emprestimos/${id}`);
      setEmprestimos(emprestimos.filter((emprestimo) => emprestimo._id !== id));
      alert('Empréstimo finalizado com sucesso!');
    } catch (error) {
      console.error('Erro ao finalizar empréstimo:', error);
    }
  };

  return (
    <div>
      <h2>Empréstimos</h2>
      <button onClick={loadEmprestimos}>Carregar Empréstimos</button>
      <table>
        <thead>
          <tr>
            <th>Usuário</th>
            <th>Livro</th>
            <th>Data de Devolução</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {emprestimos.map((emprestimo) => (
            <tr key={emprestimo._id}>
              <td>{emprestimo.usuario.nome}</td>
              <td>{emprestimo.livro.titulo}</td>
              <td>{new Date(emprestimo.dataDevolucao).toLocaleDateString()}</td>
              <td>
                {!emprestimo.devolvido && (
                  <button onClick={() => finalizarEmprestimo(emprestimo._id)}>
                    Finalizar Empréstimo
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Emprestimos;
