import 'Relatorios.css'; // Importando o CSS da página Relatórios
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Relatorios() {
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);
  const [usuariosComPendencias, setUsuariosComPendencias] = useState([]);

  useEffect(() => {
    // Obter livros mais emprestados
    axios
      .get('http://localhost:3000/api/emprestimos/livros-mais-emprestados')
      .then(res => setLivrosMaisEmprestados(res.data))
      .catch(err => console.error('Erro ao buscar livros mais emprestados:', err));

    // Obter usuários com pendências
    axios
      .get('http://localhost:3000/api/emprestimos/usuarios-com-pendencias')
      .then(res => setUsuariosComPendencias(res.data))
      .catch(err => console.error('Erro ao buscar usuários com pendências:', err));
  }, []);

  const registrarDevolucao = (id) => {
    axios
      .put(`http://localhost:3000/api/emprestimos/${id}`)
      .then(res => {
        alert('Devolução registrada com sucesso!');
        window.location.reload(); // Atualiza a página
      })
      .catch(err => console.error('Erro ao registrar devolução:', err));
  };

  return (
    <div>
      <h1>Relatórios</h1>

      <h2>Livros Mais Emprestados</h2>
      <ul>
        {livrosMaisEmprestados.map((item, index) => (
          <li key={index}>
            {item.livro
              ? `${item.livro.titulo} - ${item.livro.autor} (Emprestado ${item.count} vezes)`
              : `Livro não encontrado (Emprestado ${item.count} vezes)`}
          </li>
        ))}
      </ul>

      <h2>Usuários com Pendências</h2>
      <ul>
        {usuariosComPendencias.map((pendencia, index) => (
          <li key={index}>
            {pendencia.usuario?.nome} - {pendencia.livro?.titulo}{' '}
            <button onClick={() => registrarDevolucao(pendencia._id)}>Registrar Devolução</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Relatorios;
