import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Emprestimos.css';

const Emprestimos = () => {
  const [emprestimos, setEmprestimos] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [livros, setLivros] = useState([]);
  const [novoEmprestimo, setNovoEmprestimo] = useState({
    usuarioId: '',
    livroId: '',
    dataDevolucao: '',
  });

  // Função para buscar empréstimos
  const fetchEmprestimos = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/emprestimos');
      setEmprestimos(response.data);
    } catch (error) {
      console.error('Erro ao buscar empréstimos:', error);
    }
  };

  // Função para buscar usuários
  const fetchUsuarios = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/usuarios');
      setUsuarios(response.data);
    } catch (error) {
      console.error('Erro ao buscar usuários:', error);
    }
  };

  // Função para buscar livros
  const fetchLivros = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/livros');
      setLivros(response.data);
    } catch (error) {
      console.error('Erro ao buscar livros:', error);
    }
  };

  // Função para registrar um novo empréstimo
  const registrarEmprestimo = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/emprestimos', novoEmprestimo);
      setNovoEmprestimo({ usuarioId: '', livroId: '', dataDevolucao: '' });
      fetchEmprestimos(); // Atualizar a lista
    } catch (error) {
      console.error('Erro ao registrar empréstimo:', error);
    }
  };

  // Função para finalizar (devolver) um empréstimo
  const finalizarEmprestimo = async (id) => {
    try {
      await axios.put(`http://localhost:3000/api/emprestimos/${id}`);
      fetchEmprestimos(); // Atualizar a lista
    } catch (error) {
      console.error('Erro ao finalizar empréstimo:', error);
    }
  };

  // Carregar os dados ao montar o componente
  useEffect(() => {
    fetchEmprestimos();
    fetchUsuarios();
    fetchLivros();
  }, []);

  return (
    <div className="emprestimos-container">
      <h1>Gerenciamento de Empréstimos</h1>

      {/* Formulário para registrar empréstimos */}
      <form onSubmit={registrarEmprestimo} className="emprestimos-form">
        <h2>Registrar Novo Empréstimo</h2>
        <select
          value={novoEmprestimo.usuarioId}
          onChange={(e) => setNovoEmprestimo({ ...novoEmprestimo, usuarioId: e.target.value })}
          required
        >
          <option value="">Selecione o Usuário</option>
          {usuarios.map((usuario) => (
            <option key={usuario._id} value={usuario._id}>
              {usuario.nome}
            </option>
          ))}
        </select>
        <select
          value={novoEmprestimo.livroId}
          onChange={(e) => setNovoEmprestimo({ ...novoEmprestimo, livroId: e.target.value })}
          required
        >
          <option value="">Selecione o Livro</option>
          {livros.map((livro) => (
            <option key={livro._id} value={livro._id}>
              {livro.titulo}
            </option>
          ))}
        </select>
        <input
          type="date"
          value={novoEmprestimo.dataDevolucao}
          onChange={(e) => setNovoEmprestimo({ ...novoEmprestimo, dataDevolucao: e.target.value })}
          required
        />
        <button type="submit">Registrar</button>
      </form>

      {/* Lista de empréstimos ativos */}
      <h2>Empréstimos Ativos</h2>
      <ul className="emprestimos-lista">
        {emprestimos.map((emprestimo) => (
          <li key={emprestimo._id}>
            <span>
              <strong>Usuário:</strong> {emprestimo.usuario?.nome} - <strong>Livro:</strong> {emprestimo.livro?.titulo} - <strong>Data de Devolução:</strong> {new Date(emprestimo.dataDevolucao).toLocaleDateString()}
            </span>
            <button onClick={() => finalizarEmprestimo(emprestimo._id)}>Finalizar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Emprestimos;
