import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RelatorioLivrosMaisEmprestados = () => {
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);

  useEffect(() => {
    const fetchLivrosMaisEmprestados = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/relatorios/livros-mais-emprestados');
        setLivrosMaisEmprestados(response.data);
      } catch (error) {
        console.error('Erro ao buscar relatórios de livros mais emprestados:', error);
      }
    };

    fetchLivrosMaisEmprestados();
  }, []);

  return (
    <div>
      <h1>Relatório: Livros Mais Emprestados</h1>
      <ul>
        {livrosMaisEmprestados.map((livro) => (
          <li key={livro._id}>
            <strong>{livro.livro.titulo}</strong> - {livro.count} empréstimos
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RelatorioLivrosMaisEmprestados;
