import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Emprestimos = () => {
  const [emprestimos, setEmprestimos] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [livros, setLivros] = useState([]);
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);
  const [usuariosComPendencia, setUsuariosComPendencia] = useState([]);
  const [novoEmprestimo, setNovoEmprestimo] = useState({
    usuarioId: '',
    livroId: '',
    dataDevolucao: '',
  });

  const fetchEmprestimos = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/emprestimos');
      setEmprestimos(response.data || []); // Garante que seja uma lista
    } catch (error) {
      console.error('Erro ao buscar empréstimos:', error);
    }
  };

  const registrarEmprestimo = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3000/api/emprestimos', novoEmprestimo);
      setNovoEmprestimo({ usuarioId: '', livroId: '', dataDevolucao: '' });
      fetchEmprestimos();
    } catch (error) {
      console.error('Erro ao registrar empréstimo:', error);
    }
  };

  const finalizarEmprestimo = async (id) => {
    try {
      await axios.put(`http://localhost:3000/api/emprestimos/${id}`, { devolvido: true });
      fetchEmprestimos();
    } catch (error) {
      console.error('Erro ao finalizar empréstimo:', error);
    }
  };

  const fetchUsuarios = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/usuarios');
      setUsuarios(response.data || []);
    } catch (error) {
      console.error('Erro ao buscar usuários:', error);
    }
  };

  const fetchLivros = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/livros');
      setLivros(response.data || []);
    } catch (error) {
      console.error('Erro ao buscar livros:', error);
    }
  };

  const fetchLivrosMaisEmprestados = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/emprestimos/livros-mais-emprestados');
      setLivrosMaisEmprestados(response.data || []);
    } catch (error) {
      console.error('Erro ao buscar livros mais emprestados:', error);
    }
  };

  const fetchUsuariosComPendencia = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/usuarios/pendencia');
      setUsuariosComPendencia(response.data || []);
    } catch (error) {
      console.error('Erro ao buscar usuários com pendência:', error);
    }
  };

  useEffect(() => {
    fetchEmprestimos();
    fetchUsuarios();
    fetchLivros();
    fetchLivrosMaisEmprestados();
    fetchUsuariosComPendencia();
  }, []);

  return (
    <div className="emprestimos-container">
      <h1>Gerenciamento de Empréstimos</h1>

      <form onSubmit={registrarEmprestimo} className="emprestimos-form">
        <h2>Registrar Novo Empréstimo</h2>
        <select
          value={novoEmprestimo.usuarioId}
          onChange={(e) => setNovoEmprestimo({ ...novoEmprestimo, usuarioId: e.target.value })}
          required
        >
          <option value="">Selecione o Usuário</option>
          {usuarios.map((usuario) => (
            <option key={usuario._id} value={usuario._id}>
              {usuario.nome}
            </option>
          ))}
        </select>
        <select
          value={novoEmprestimo.livroId}
          onChange={(e) => setNovoEmprestimo({ ...novoEmprestimo, livroId: e.target.value })}
          required
        >
          <option value="">Selecione o Livro</option>
          {livros.map((livro) => (
            <option key={livro._id} value={livro._id}>
              {livro.titulo}
            </option>
          ))}
        </select>
        <input
          type="date"
          value={novoEmprestimo.dataDevolucao}
          onChange={(e) => setNovoEmprestimo({ ...novoEmprestimo, dataDevolucao: e.target.value })}
          required
        />
        <button type="submit">Registrar</button>
      </form>

      <h2></h2>
      <ul className="emprestimos-lista">
        {emprestimos.length === 0 ? (
          <p>Nenhum empréstimo ativo no momento.</p>
        ) : (
          emprestimos.map((emprestimo) => (
            <li key={emprestimo._id}>
              <span>
                <strong>Usuário:</strong> {emprestimo.usuario?.nome || 'Desconhecido'} - 
                <strong>Livro:</strong> {emprestimo.livro?.titulo || 'Desconhecido'} - 
                <strong>Data de Devolução:</strong> {emprestimo.dataDevolucao ? new Date(emprestimo.dataDevolucao).toLocaleDateString() : 'Não informado'}
              </span>
              <button onClick={() => finalizarEmprestimo(emprestimo._id)}>Finalizar</button>
            </li>
          ))
        )}
      </ul>

      <h2>Livros Mais Emprestados</h2>
      <ul>
        {livrosMaisEmprestados.length === 0 ? (
          <p>Não há livros mais emprestados no momento.</p>
        ) : (
          livrosMaisEmprestados.map((item, index) => (
            <li key={index}>
              <strong>{item.titulo}</strong> - {item.count} empréstimos
            </li>
          ))
        )}
      </ul>

      <h2>Usuários com Pendência</h2>
      <ul>
        {usuariosComPendencia.length === 0 ? (
          <p>Não há usuários com pendências no momento.</p>
        ) : (
          usuariosComPendencia.map((usuario) => (
            <li key={usuario._id}>
              <strong>{usuario.nome}</strong> - Pendência: {usuario.pendencia}
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default Emprestimos;
