import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Relatorios = () => {
  const [livrosMaisEmprestados, setLivrosMaisEmprestados] = useState([]);
  const [usuariosComPendencias, setUsuariosComPendencias] = useState([]);

  useEffect(() => {
    // Requisição para Livros mais emprestados
    axios.get('http://localhost:3000/emprestimos/livros-mais-emprestados')
      .then(response => {
        setLivrosMaisEmprestados(response.data);
      })
      .catch(error => {
        console.error('Erro ao buscar livros mais emprestados:', error);
      });

    // Requisição para Usuários com pendências
    axios.get('http://localhost:3000/emprestimos/usuarios-com-pendencias')
      .then(response => {
        setUsuariosComPendencias(response.data);
      })
      .catch(error => {
        console.error('Erro ao buscar usuários com pendências:', error);
      });
  }, []);

  return (
    <div>
      <h1>Relatórios</h1>

      <h2>Livros mais Emprestados</h2>
      <ul>
        {livrosMaisEmprestados.map((item, index) => (
          <li key={index}>{item._id.titulo} - {item.count} vezes</li>
        ))}
      </ul>

      <h2>Usuários com Pendências</h2>
      <ul>
        {usuariosComPendencias.map((pendencia, index) => (
          <li key={index}>
            {pendencia.usuario.nome} - {pendencia.livro.titulo} - Não devolvido
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Relatorios;
